var fs = require('fs'),
    ft = require('../build/Release/freetype2'),
    f = ft.New_Memory_Face(fs.readFileSync(process.argv[2]), 0);


ft.Set_Char_Size(f, 0, 40 << 6, 0, 0);
ft.Load_Glyph(f, 10, ft.LOAD_DEFAULT);
ft.Render_Glyph(f.glyph, ft.RENDER_MODE_MONO);
var buffer = f.glyph.bitmap.buffer;
var bufferCopy = new Buffer(buffer);
